import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
  ImageSourcePropType,
} from 'react-native';

import FlipCard from 'react-native-flip-card';
import creditcardutils from 'creditcardutils';

type Props = {
  /** Number to display on the front of the card */
  number: number | string;

  /** Name to display on the front of the card */
  name: string;

  /** CVC to display on the back of the card */
  cvc: number | string;

  /** Expiration Date to display on the front of the card. displayed as given, so it should be formatted */
  expiration: string;

  /** Year for `Customer Since` */
  since?: number | string;

  /** Additional styles to apply to the front of the card */
  frontStyles?: object;

  /** Additional styles to apply to the back of the card */
  backStyles?: object;

  /** Alternate image to use for the front of the card */
  frontImage?: ImageSourcePropType;

  /** Alternate image to use for the back of the card */
  backImage?: ImageSourcePropType;

  /** Additional styles to apply to the component from `react-native-flip-card` */
  cardStyles?: object;

  /** Height for card. default: 190 */
  height?: number;

  /** Width for card. default: 300 */
  width?: number;

  /** Base font size to use. Will scale all displayed text. default: 20 */
  fontSize?: number;

  /** Text font color. Default: #FFFFFF (White) */
  fontColor?: string;

  /** Friction for the card flip. I couldn't figure out what this does, but I'm just passing it to `react-native-flip-card` if anyone wants it */
  friction?: number;

  /** Is the card flipped? Use this to programmatically `flip` the card. */
  flipped?: boolean;

  /** Border Radius to use on the images */
  borderRadius?: number;
} & typeof defaultProps;

const defaultProps = {
  height: 190,
  width: 300,
  fontSize: 15,
  fontColor: '#FFFFFF',
  friction: 6,
  flipped: false,
  borderRadius: 20,
  frontImage: require('./assets/images/card-front.png'),
  backImage: require('./assets/images/card-back.png'),
};

const CreditCardDisplay = (props: Props) => {
  let cardTypeIcon = null;

  switch (creditcardutils.parseCardType(props.number as string)) {
    case 'amex':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'dinersclub':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'discover':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'jcb':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'maestro':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'mastercard':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'unionpay':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'visa':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'visaelectron':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
  }

  return (
    <View style={{ height: props.height, width: props.width }}>
      <FlipCard
        style={props.cardStyles}
        flipHorizontal={true}
        flipVertical={false}
        flip={props.flipped}
      >
        <View style={props.frontStyles}>
          <View style={{ height: props.height, width: props.width }}>
            <ImageBackground
              source={props.frontImage}
              style={styles.imageBackground}
              imageStyle={{ borderRadius: props.borderRadius }}
            >
              <View style={styles.imageContainer}>
                <View style={{ flexGrow: 1 }} />

                <Text
                  style={{
                    fontSize: props.fontSize,
                    alignSelf: 'center',
                    color: props.fontColor,
                  }}
                >
                  {creditcardutils.formatCardNumber(String(props.number))}
                </Text>

                <View style={styles.rowContainer}>
                  <View style={styles.groupContainer}>
                    {props.since && (
                      <>
                        <Text
                          style={{
                            ...styles.groupLabel,
                            fontSize: props.fontSize * 0.7,
                            color: props.fontColor,
                            textAlign: 'right',
                          }}
                        >
                          CUSTOMER{'\n'}SINCE
                        </Text>

                        <Text
                          style={{
                            fontSize: props.fontSize * 0.7,
                            color: props.fontColor,
                          }}
                        >
                          {props.since}
                        </Text>
                      </>
                    )}
                  </View>

                  <View style={styles.groupContainer}>
                    {props.expiration && (
                      <>
                        <Text
                          style={{
                            ...styles.groupLabel,
                            fontSize: props.fontSize * 0.7,
                            color: props.fontColor,
                            textAlign: 'center',
                          }}
                        >
                          VALID{'\n'}THRU
                        </Text>

                        <Text
                          style={{
                            fontSize: props.fontSize * 0.7,
                            color: props.fontColor,
                          }}
                        >
                          {creditcardutils.formatCardExpiry(props.expiration)}
                        </Text>
                      </>
                    )}
                  </View>
                  <View style={styles.cardTypeIconContainer}>
                    {cardTypeIcon && (
                      <Image
                        source={cardTypeIcon}
                        style={styles.cardTypeIcon}
                        resizeMode="contain"
                      />
                    )}
                  </View>
                </View>
                <Text
                  style={{
                    fontSize: props.fontSize,
                    marginTop: 5,
                    color: props.fontColor,
                  }}
                >
                  {props.name}
                </Text>
              </View>
            </ImageBackground>
          </View>
        </View>

        <View style={props.backStyles}>
          <View style={{ height: props.height, width: props.width }}>
            <ImageBackground
              source={props.backImage}
              style={styles.imageBackground}
              imageStyle={{ borderRadius: 15 }}
            >
              <View style={{ height: '45%' }} />

              <View style={{ flexDirection: 'row' }}>
                <View style={{ width: '82%' }} />

                <Text style={{ fontSize: props.fontSize, alignSelf: 'center' }}>
                  {props.cvc}
                </Text>
              </View>
            </ImageBackground>
          </View>
        </View>
      </FlipCard>
    </View>
  );
};

export default CreditCardDisplay;

const styles = StyleSheet.create({
  imageBackground: {
    height: '100%',
    width: '100%',
  },
  groupContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginRight: '2%',
  },
  groupLabel: {
    textAlignVertical: 'center',
    marginRight: '2%',
  },
  imageContainer: {
    padding: 15,
    flex: 1,
  },
  cardTypeIconContainer: {
    justifyContent: 'center',
    height: 27,
    width: 27 * (125 / 80),
    alignItems: 'center',
  },
  cardTypeIcon: {
    height: 25,
    width: 25 * (125 / 80),
    backgroundColor: 'white',
  },
  rowContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
});

CreditCardDisplay.defaultProps = defaultProps;
